<?php/**Базовый класс сайта*/abstract class C_Base extends C_Controller{	protected $modelUser;	 // модель пользователей	protected $title;        // заголовок страницы	protected $needLogin;    // необходима ли авторизация	protected $authorization;// состояние авторизации	protected $user;         // авторизованный пользователь	protected $fotoUser;     // фото пользователя	//protected $idUser;     // id пользователя	protected $trueAdmin;    // авторизованный пользователь - это администратор	protected $dataTemplate; // массив шаблонов: [0]-имя файла, [1..n]-параметры							 // [[],[],...,[]]	function __construct()	{		$this->needLogin = false;		$this->modelUser = M_Users::getInstance();		$this->authorization = $this->modelUser->passportUser["authorization"];		$this->user 		 = $this->modelUser->passportUser["login"];		$this->trueAdmin 	 = $this->modelUser->passportUser["trueAdmin"];		$this->fotoUser 	 = $this->modelUser->passportUser["foto_user"];	}	/**Функция отрабатывающая до основного метода*/	protected function before()	{		$this->title = 'Visit card :: ';		if($this->needLogin && !$this->authorization) {			$this->redirect('/auth/login');		}	}	/**Генерация базового шаблона*/	public function render()	{		$page = "";		foreach ($this->dataTemplate as $values) {			$templateFile = array_shift($values);			$page .= $this->Template($templateFile, $values);		}		echo $page;	}}