<?php/**Базовый класс сайта*/abstract class C_Base extends C_Controller{	protected $title;        // заголовок страницы	protected $content;      // содержание страницы	protected $needLogin;    // необходима ли авторизация	protected $user;         // авторизованный пользователь ||	protected $dataTemplate; // массив данных шаблона: имя файла и параметры	function __construct()	{		$this->needLogin = false;	}	/**Функция отрабатывающая до основного метода*/	protected function before()	{		/*if($this->needLogin && $this->user === null)			$this->redirect('/auth/login');*/		$this->title = 'Visit card:: ';		$this->content = '';	}	/**Генерация базового шаблона*/	public function render()	{		$page = "";		foreach ($this->dataTemplate as $key => $values) {			$page .= $this->Template($key, $values);		}		echo $page;	}}