<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;			// заголовок страницы	protected $content;			// содержание страницы	protected $needLogin;		// необходима ли авторизация	protected $user;			// авторизованный пользователь ||	protected $varsTemplate;	// переменные для шаблона	protected $fileTemplate;	// файл шаблона	function __construct()	{			$this->needLogin = false;	}		protected function before()	{		/*if($this->needLogin && $this->user === null)			$this->redirect('/auth/login');*/			$this->title = 'Visit card:: ';		$this->content = '';	}	/**Генерация базового шаблона*/	public function render()	{		$page = $this->Template($this->fileTemplate, $this->varsTemplate);		echo $page;	}	}