<?php/**Базовый класс сайта*/abstract class C_Base extends C_Controller{	protected $title;        // заголовок страницы	protected $content;      // содержание страницы	protected $needLogin;    // необходима ли авторизация	protected $authorization;// состояние авторизации	protected $user;         // авторизованный пользователь ||	protected $dataTemplate; // массив шаблонов: [0]-имя файла, [1..n]-параметры							 // [[],[],...,[]]	function __construct()	{		$this->needLogin = false;		$this->authorization = false;	}	/**Функция отрабатывающая до основного метода*/	protected function before()	{		/*if($this->needLogin && $this->user === null)			$this->redirect('/auth/login');*/		$this->title = 'Visit card :: ';		$this->content = '';	}	/**Генерация базового шаблона*/	public function render()	{		$page = "";		foreach ($this->dataTemplate as $values) {			$templateFile=array_shift($values);			$page .= $this->Template($templateFile, $values);		}		echo $page;	}}