<?php//// Конттроллер страниц.//class C_Home extends C_Base{	//	// Конструктор.	//	public function __construct()	{		parent::__construct();	}	public function before()	{		//$this->needLogin = true; // раскоментируйте, чтобы закрыть доступ ко всем страницам данного контроллера		parent::before();		$this->title .= "Home";	}	public function action_index()	{		$countImgMenu = 10;		$this->dataTemplate = [[			"template" => PATH_VIEW . "template/header.php",			"title" => $this->title,			"logo" => "logo.png",			"authorization" => $this->authorization,			"login" => "Kuty",			"fotoUser" => "7.jpg",		]];		for ($i = 0; $i < $countImgMenu; $i++) {			$this->dataTemplate[] = [				"template" => PATH_VIEW . "template/table_menu.php",				"imgMenu" => "7.jpg",				"descriptionImgMenu" => "описание",				"linkimgMenu" => "https://yandex.ru",];		}		$this->dataTemplate[] = [			"template" => PATH_VIEW . "template/footer.php",			"logo" => "logo.png",		];	}}