<?php/**Класс домашней страницы*/class C_Home extends C_Base{	public function __construct()	{		parent::__construct();	}	public function before()	{		//$this->needLogin = true; // раскоментируйте, чтобы закрыть доступ ко всем страницам данного контроллера		parent::before();		$this->title .= "Home";	}/** * Формирование*/	public function action_index()	{		//Подготовка Header		$modelHome = M_Home::getInstance();		$dataBD =  $modelHome->imgMenuGet();		$this->dataTemplate = [[			"template" => PATH_TEMPLATE . "header.php",			"title" => $this->title,			"logo" => "logo.png",			"authorization" => $this->authorization,			"login" => $this->userLogin,			"userPhoto" => $this->userPhoto,		]];		//Подготовка графического меню		foreach ($dataBD as $value) {			$img_file = $value['img_file'];			$description = $value['description'];			$link_page = $value['link_page'];			//todo Потом удалить			//---------------------------------------			$class="focus-green pic-green";			if ($link_page==="/"){				$class="focus-red pic-red";			}			//---------------------------------------			$this->dataTemplate[] = [				"template" => PATH_TEMPLATE . "table_menu.php",				"imgFile" => $img_file,				"descriptionImgMenu" => $description,				//todo Потом удалить				//---------------------------------------				"class" => $class,				//---------------------------------------				"linkimgMenu" => $link_page,];		}		//Подготовка Footer		$this->dataTemplate[] = [			"template" => PATH_TEMPLATE . "footer.php",			"logo" => "logo.png",		];	}}